        reminder: false,
    },
    {
        id: 3,
        text : 'CCC',
        day : 'Feb 5th at 2:30pm',
        reminder: false,
    }

  ])

  // delete Task
  const deleteTask = (id) => {
    console.log('delete',id)
  }

  return (
    <div className='container'>
      <Header/>

      <Tasks tasks={tasks} onDelete={deleteTask}/>
    </div>

  )
}

